INSERT INTO `message_receive_tache` VALUES (1, '0', 'need_answer', ' select 1 from message_receive a where a.id=#{id} and ((a.msg_type=\'100\'  and a.msg_sub_type=\'1\' )or a.msg_sub_type=\'10002\') and a.from_group_id not in (select b.codea from message_receive_tache_param b where b.param_key in( \'answer_group_id\',\'robot_manager_group\')) AND a.message not IN (\r\n	SELECT\r\n		b.codea\r\n	FROM\r\n		message_receive_tache_param b\r\n	WHERE\r\n		b.param_key = \'invite_group\'\r\n)\r\n', '用户消息待回复', NULL);
INSERT INTO `message_receive_tache` VALUES (2, '0', 'answer_question', ' select 1 from message_receive a where a.id=#{id} and (a.msg_type=\'200\'  and a.msg_sub_type=\'1\' ) and a.from_group_id in (select b.codea from message_receive_tache_param b where b.param_key in ( \'answer_group_id\'))', '回复消息', NULL);
INSERT INTO `message_receive_tache` VALUES (3, '0', 'group_send_begin_pre', 'SELECT\r\n	1\r\nFROM\r\n	message_receive a\r\nWHERE\r\n	a.id = #{id}\r\nand a.msg_sub_type=\'10002\'\r\nand a.message=\'准备群发\'\r\nAND a.from_group_id IN (\r\n	SELECT\r\n		b.codea\r\n	FROM\r\n		message_receive_tache_param b\r\n	WHERE\r\n		b.param_key = \'group_send_id\'\r\n)', '开始准备群发', NULL);
INSERT INTO `message_receive_tache` VALUES (4, '0', 'group_send_pre', '\r\nSELECT\r\n	1\r\nFROM\r\n	message_receive a\r\nWHERE\r\n	a.id =#{id}\r\nand exists (select 1 from message_receive_tache_param c where c.param_key=\'group_send_state\' and c.codea=\'1\')\r\nand a.msg_sub_type in (\'1\',\'3\',\'49\')\r\nAND a.from_group_id IN (\r\n	SELECT\r\n		b.codea\r\n	FROM\r\n		message_receive_tache_param b\r\n	WHERE\r\n		b.param_key = \'group_send_id\'\r\n)\r\n', '群发准备中', NULL);
INSERT INTO `message_receive_tache` VALUES (5, '0', 'group_send', 'SELECT\r\n	1\r\nFROM\r\n	message_receive a\r\nWHERE\r\n	a.id =#{id}\r\nand a.message=\'开始群发\'\r\nand exists (select 1 from message_receive_tache_param c where c.param_key=\'group_send_state\' and c.codea=\'1\')\r\nand a.msg_sub_type=\'10002\'\r\nAND a.from_group_id IN (\r\n	SELECT\r\n		b.codea\r\n	FROM\r\n		message_receive_tache_param b\r\n	WHERE\r\n		b.param_key = \'group_send_id\'\r\n)', '群发', NULL);
INSERT INTO `message_receive_tache` VALUES (6, '0', 'group_send_stop', 'SELECT\r\n	1\r\nFROM\r\n	message_receive a\r\nWHERE\r\n	a.id = #{id}\r\nand a.msg_sub_type=\'10002\'\r\nand a.message=\'停止群发\'\r\nAND a.from_group_id IN (\r\n	SELECT\r\n		b.codea\r\n	FROM\r\n		message_receive_tache_param b\r\n	WHERE\r\n		b.param_key = \'group_send_id\'\r\n)', '停止群发', NULL);
INSERT INTO `message_receive_tache` VALUES (7, '0', 'qa_introduce', 'SELECT\r\n	1\r\nFROM\r\n	message_receive a\r\nWHERE\r\n	a.id = #{id}\r\nand a.message in (\'q\',\'SWTC问答\')\r\nand a.msg_type=\'200\'  and a.msg_sub_type=\'1\' ', '问答介绍', NULL);
INSERT INTO `message_receive_tache` VALUES (8, '0', 'qa_auto', 'SELECT\r\n	1\r\nFROM\r\n	message_receive a,message_receive_tache_param b \r\nWHERE\r\n	a.id = #{id}\r\nand a.message=b.coded and b.param_key=\'qa_data\'\r\nand a.msg_type=\'200\'  and a.msg_sub_type=\'1\' ', '问答', NULL);
INSERT INTO `message_receive_tache` VALUES (9, '0', 'welcome', 'SELECT\r\n	1\r\nFROM\r\n	message_receive a\r\nWHERE\r\n	a.id = #{id}\r\nAND a.msg_type = \'400\'\r\nAND a.msg_sub_type = \'0\'\r\nAND a.col1 != \'\'\r\nAND a.col2 != \'\'', 'welcome', NULL);
INSERT INTO `message_receive_tache` VALUES (10, '0', 'invite_group', 'SELECT\r\n	1\r\nFROM\r\n	message_receive a\r\nWHERE\r\n	a.id = #{id}\r\nAND a.msg_type = \'100\'\r\nAND a.msg_sub_type = \'1\'\r\nAND a.message IN (\r\n	SELECT\r\n		b.codea\r\n	FROM\r\n		message_receive_tache_param b\r\n	WHERE\r\n		b.param_key = \'invite_group\'\r\n)', '邀请进群', NULL);
INSERT INTO `message_receive_tache` VALUES (11, '0', 'invite_group', 'SELECT\r\n	1\r\nFROM\r\n	message_receive a\r\nWHERE\r\n	a.id = #{id}\r\nAND a.msg_type = \'100\'\r\nAND (\r\n	(\r\n		a.msg_sub_type = \'10000\'\r\n		AND a.message LIKE \'%把你添加到通讯录，现在可以开始聊天了%\'\r\n	)\r\n	OR (\r\n		a.msg_sub_type = \'1\'\r\n		AND a.message = \'我通过了你的朋友验证请求，现在我们可以开始聊天了\'\r\n	)\r\n)', '邀请进群', NULL);
INSERT INTO `message_receive_tache` VALUES (12, '0', 'auto_answer', 'SELECT\r\n	1\r\nFROM\r\n	message_receive a\r\nWHERE\r\n	a.id = #{id}\r\nAND a.msg_type = \'200\'\r\nAND a.msg_sub_type = \'1\'\r\nAND a.from_group_id IN (\r\n	SELECT\r\n		codea\r\n	FROM\r\n		message_receive_tache_param\r\n	WHERE\r\n		param_key = \'auto_answer_group\'\r\n)', '自动回复群', NULL);
